--Create tables and add primary and foregin keys
create table teams( 
Team_ID integer NOT NULL,
Team_name VARCHAR2(30),
YearOfFound integer,
CONSTRAINT team_pk PRIMARY KEY (Team_ID));

create table stadiums( 
Stadium_ID integer NOT NULL,
St_name VARCHAR2(30),
City VARCHAR2(30),
Capacity integer,
TeamID integer NOT NULL,
CONSTRAINT stadium_pk PRIMARY KEY (Stadium_ID)
);

ALTER TABLE stadiums
ADD CONSTRAINT stadium_fk1 
  FOREIGN KEY (TeamID) 
  REFERENCES TEAMS (TEAM_ID);

create table player( 
Player_ID integer  NOT NULL,
Pl_name VARCHAR2(30),
Pl_surname VARCHAR2(30),
TeamID integer NOT NULL,
CONSTRAINT player_pk PRIMARY KEY (Player_ID)
);

ALTER TABLE player
ADD CONSTRAINT player_fk1 
  FOREIGN KEY (TeamID) 
  REFERENCES TEAMS (TEAM_ID);

create table matches( 
Team_A integer NOT NULL,
Team_B integer NOT NULL,
M_date date NOT NULL,
CONSTRAINT matches_pk PRIMARY KEY (Team_A, Team_B, M_date));

ALTER TABLE matches
ADD CONSTRAINT matches_fk1 
  FOREIGN KEY (Team_A) 
  REFERENCES TEAMS (TEAM_ID);

ALTER TABLE matches
ADD CONSTRAINT matches_fk2 
  FOREIGN KEY (Team_B) 
  REFERENCES TEAMS (TEAM_ID);

create table player_stats ( 
Team_A_ID INTEGER NOT NULL,
Team_B_ID INTEGER NOT NULL,
M_date DATE NOT NULL,
Player_ID INTEGER NOT NULL,
MinPlayed INTEGER,
Goals INTEGER,
Shot_on INTEGER,
Shot_off INTEGER,
Penalty INTEGER,
Pass_OK INTEGER,
Pass_KO INTEGER,
CONSTRAINT player_stats_pk PRIMARY KEY (Team_A_ID, Team_B_ID, M_date, Player_ID));

ALTER TABLE player_stats
ADD CONSTRAINT player_stats_fk1 
  FOREIGN KEY (Player_ID) 
  REFERENCES player (Player_ID);
  
ALTER TABLE player_stats
ADD CONSTRAINT player_stats_fk2 
  FOREIGN KEY (Team_A_ID, Team_B_ID, M_date) 
  REFERENCES Matches (Team_A, Team_B, M_date);

