--Inserts stats into stage

INSERT INTO fact_stats_stage (m_date, Player_ID, Team_A_ID, Team_B_ID, min_played, goals, shot_on, shot_off, penalty, pass_ok, pass_ko)
SELECT m_date, Player_ID, Team_A_ID, Team_B_ID, MinPlayed, Goals, Shot_on, Shot_off, Penalty, Pass_ok, Pass_ko
FROM player_stats;

--Updates each relevant table with surrogate keys

UPDATE fact_stats_stage SET Player_SK = (SELECT Player_SK FROM DimPlayer_stage 
WHERE (DimPlayer_stage.Player_ID = fact_stats_stage.Player_ID));

UPDATE fact_stats_stage SET Team_SK = (SELECT Team_SK FROM DimTeam_stage
WHERE (DimTeam_stage.Team_ID = fact_stats_stage.Team_A_ID));

UPDATE fact_stats_stage SET Opponent_SK = (SELECT Team_SK FROM DimTeam_stage
WHERE (DimTeam_stage.Team_ID = fact_stats_stage.Team_B_ID));

UPDATE fact_stats_stage SET Stadium_SK = (SELECT Stadium_SK FROM DimStadium_stage
WHERE (DimStadium_stage.TeamID = fact_stats_stage.Team_A_ID));

UPDATE fact_stats_stage SET date_SK = (SELECT date_SK FROM DimTime_stage
