INSERT INTO DimPlayer_stage (Player_ID, Player_name, Player_surname)
SELECT DISTINCT Player_ID, Player_firstname, Player_surname
FROM Player_Stats_Import_2 CSV2 
WHERE NOT EXISTS (SELECT * 
                  FROM DimPlayer_stage dpls 
                  WHERE dpls.Player_ID = CSV2.Player_ID);

--Inserts facts that dont exist to stats stage

INSERT INTO fact_stats_stage (Team_A_ID, Team_B_ID, M_date, Player_ID, Min_Played, Goals, Shot_on, Shot_off, Penalty, Pass_OK, Pass_KO)
SELECT DISTINCT CSV2.Team_ID, CSV2.Opposition_ID, CSV2.m_date, CSV2.Player_ID, CSV2.Mins_Played, CSV2.Goals, CSV2.Shots_On_Target, CSV2.Shots_Off_Target, CSV2.Penalty_Goals, CSV2.Total_Successful_Passes, CSV2.Total_Unsuccessful_Passes
FROM Player_Stats_Import_2 CSV2
WHERE NOT EXISTS (SELECT * 
                  FROM fact_stats_stage fss
                  WHERE fss.Player_ID = CSV2.Player_ID AND fss.m_date = CSV2.m_date);

--Inserts stats from stage to fact_stats

INSERT INTO fact_stats (date_sk, player_sk, team_sk, opponent_sk, stadium_sk, min_played, goals, shot_on, shot_off, penalty, pass_ok, pass_ko)
SELECT FSS.date_sk, FSS.player_sk, FSS.team_sk, FSS.opponent_sk, FSS.stadium_sk, FSS.min_played, FSS.goals, FSS.shot_on, FSS.shot_off, FSS.penalty, FSS.pass_ok, FSS.pass_ko
FROM fact_stats_stage FSS
WHERE NOT EXISTS (SELECT * 
                  FROM fact_stats_stage fss2
                  WHERE FSS.Player_SK = fss2.Player_SK AND FSS.Date_SK = fss.Date_SK);
              
--Updates relevant keys in each Dim table

UPDATE fact_stats_stage SET Player_SK = (SELECT Player_SK FROM DimPlayer_stage 
WHERE (DimPlayer_stage.Player_ID = fact_stats_stage.Player_ID));

UPDATE fact_stats_stage SET Team_SK = (SELECT Team_SK FROM DimTeam_stage
WHERE (DimTeam_stage.Team_ID = fact_stats_stage.Team_A_ID));

UPDATE fact_stats_stage SET Opponent_SK = (SELECT Team_SK FROM DimTeam_stage
WHERE (DimTeam_stage.Team_ID = fact_stats_stage.Team_B_ID));

UPDATE fact_stats_stage SET Stadium_SK = (SELECT Stadium_SK FROM DimStadium_stage
WHERE (DimStadium_stage.TeamID = fact_stats_stage.Team_A_ID));

UPDATE fact_stats_stage SET date_SK = (SELECT date_SK FROM DimTime_stage
WHERE (DimTime_stage.m_date = fact_stats_stage.m_date));


