--2 Complex Queries for Dim Model

--Players who scored hatricks
SELECT DISTINCT player_name, player_surname, team_name, year
FROM DimPlayer
JOIN fact_stats ON fact_stats.player_sk = DimPlayer.player_sk
JOIN DimTeam ON fact_stats.team_sk = DimTeam.team_sk
JOIN DimTime ON fact_stats.date_sk = DimTime.date_sk
WHERE goals = 3
ORDER BY player_surname;

--Penalty information, stadium, player, year etc
SELECT DISTINCT player_name, player_surname, team_name, stadium_name, year, penalty
FROM DimPlayer
JOIN fact_stats ON fact_stats.player_sk = DimPlayer.player_sk
JOIN DimStadium ON fact_stats.stadium_sk = DimStadium.stadium_sk
JOIN DimTeam ON fact_stats.team_sk = DimTeam.team_sk
JOIN DimTime ON fact_stats.date_sk = DimTime.date_sk
WHERE fact_stats.penalty > 0
ORDER BY year;